<?phpclass Truck extends CI_Controller {	var $data;		function __construct() {		parent::__construct();				$this->load->model("truck_model", "truck");				$this->data['title'] = 'Track My Truck';		$this->data['copyright'] = '© KKKGKC';	}		public function index() {		$this->load->view('list', $this->data);	}		public function search() {		$data['query'] = $this->truck->get_search();		$crops = $this->load->view('list', $data);	}		public function register() {		$this->load->view('input', $this->data);	}		public function create() {					if ($this->truck->add()):				$this->load->view("list", $this->data);			else:				return false;			endif;			}}?>