<?phpclass Truck extends CI_Controller {	var $data;		function __construct() {		parent::__construct();				$this->load->model("truck_model", "truck");				$this->data['alert'] = null;		$this->data['message'] = null;		$this->data['title'] = 'Track My Truck';		$this->data['copyright'] = '© KKKGKC';		$this->data['truckData'] = null;	}		public function index() {		$this->load->view('list', $this->data);	}		public function register() {		$this->load->view('input', $this->data);	}		public function create() {		if ($this->truck->add()):			$this->data['message'] = "Item added successfully.";			$this->data['alert'] = "alert-success";			$this->load->view("list", $this ->data);		else:			$this->data['message'] = "Item not added.";			$this->data['alert'] = "alert-danger";			$this->load->view("list", $this ->data);		endif;	}		public function edit($drNum) {		$this->data['truckData'] = $this->truck->get($drNum);				$this->load->view("input", $this->data);	}		public function update($drNum) {		if ($this->truck->update($drNum)):			$this->load->view("list", $this->data);		else:			return false;		endif;	}	public function delete($drNum) {		if ($this->truck->delete($drNum)):			$this->data['message'] = "Item deleted successfully.";			$this->data['alert'] = "alert-success";			$this->load->view("list", $this ->data);		else:			$this->data['message'] = "Item not deleted.";			$this->data['alert'] = "alert-danger";			$this->load->view("list", $this ->data);		endif;	}		public function search($offset = 0) {		$config['base_url'] = base_url()."index.php/truck/search";		$config['per_page'] = 10;		$config['uri_segment'] = '3';		$config['full_tag_open'] = '<ul class="pagination">';		$config['full_tag_close'] = '</ul>';		$config['first_link'] = '« First';		$config['first_tag_open'] = '<li class="prev page">';		$config['first_tag_close'] = '</li>';		$config['last_link'] = 'Last »';		$config['last_tag_open'] = '<li class="next page">';		$config['last_tag_close'] = '</li>';		$config['next_link'] = 'Next';		$config['next_tag_open'] = '<li class="next page">';		$config['next_tag_close'] = '</li>';		$config['prev_link'] = 'Previous';		$config['prev_tag_open'] = '<li class="prev page">';		$config['prev_tag_close'] = '</li>';		$config['cur_tag_open'] = '<li class="active"><a href="">';		$config['cur_tag_close'] = '</a></li>';		$config['num_tag_open'] = '<li class="page">';		$config['num_tag_close'] = '</li>';		$truckNum = $this->input->post("search-truckNum");		$commodity = $this->input->post("search-commodity");		$soldTo = $this->input->post("search-soldTo");		$driver = $this->input->post("search-driver");		$drNum = $this->input->post("search-drNum");				if ($truckNum == "" && $commodity == "" && $soldTo == "" && $driver == "" && $drNum == ""):			$query = $this->truck->getAll(10,$this->uri->segment(3));						$config['total_rows'] = $this->truck->totalTruck();		else:			$query = $this->truck->getSearch(10,$this->uri->segment(3));						$config['total_rows'] = $this->truck->countTruck();		endif;		$this->pagination->initialize($config);				$this->data['truckData'] = null;		if($query):			$this->data['truckData'] =  $query;		else:			$this->data['message'] = "Item does not exist in database.";			$this->data['alert'] = "alert-danger";		endif;				$this->load->view('list', $this->data);	}	}?>